function getDataProject(){var i="";$.getJSON("/data/data.json",function(a){i=a;var d=document.querySelector(".portfolio__list"),t=2.8,e=i.sort((i,a)=>a.id-i.id).map(i=>'<div class="grid-12 grid-md-4 wow fadeInUp" data-wow-delay="'+(t+=.1)+'s"><a class="image item" href="/detail.html?pid='+i.id+'"><div class="bar"><h2>'+i.name+'</h2><i></i></div><div class="main">  <div class="back"><img src="/images/project/'+i.id+"/"+i.imgMain+'" alt=""></div> <div class="tags">    <div>      <ul>'+getItem(i.tags).join("")+'</ul>    </div>  </div>  <div class="imgs"></div> </div></a></div>');d.innerHTML+=e.join("")})}window.addEventListener("load",function(){loadingScreen(),effectTitle(2.5),runWow(),getDataProject(),backToTop()});